// external dependencies
import "dotenv/config";
import "reflect-metadata";
import express from "express";

// internal dependencies
import { bootstrap } from "./bootstrap";

const app = express();

bootstrap(app);

export default app;

// external dependencies
import helmet from "helmet";
import cors from "cors";
import mongoose from "mongoose";
import express, { Application, Request, Response } from "express";

// internal dependencies
import logger from "./utils/logger";
import { connect } from "./utils/db";
import { config } from "./utils/config";
import prRoutes from "./routes/prRoutes";
import devRoutes from "./routes/devRoutes";
import authRoutes from "./routes/authRoutes";
import userRoutes from "./routes/userRoutes";
import { apiRateLimiter } from "./utils/rateLimiter";
import { errorHandler } from "./middlewares/errorHandler";
import {
  API_ENDPOINTS,
  DB_STATES,
  MESSAGES,
  STATUS_CODES,
} from "./common/constants";
import { APIError } from "./common/types";

/**
 * Initializes global middlewares
 */
function initMiddlewares(app: Application): void {
  app.use(helmet());
  app.use(cors({ origin: config.corsOrigin }));
  app.use(express.json());
  app.use(API_ENDPOINTS.API, apiRateLimiter);
}

/**
 * Registers all API routes
 */
function initRoutes(app: Application): void {
  app.use(API_ENDPOINTS.API, authRoutes);
  app.use(API_ENDPOINTS.API, userRoutes);
  app.use(API_ENDPOINTS.API, prRoutes);
  app.use(API_ENDPOINTS.API, devRoutes);
}

/**
 * Health check route for DB status and uptime
 */
function initHealthCheck(app: Application): void {
  app.get(API_ENDPOINTS.HEALTH_CHECK, async (_: Request, res: Response) => {
    try {
      const dbState = mongoose.connection.readyState;
      let dbStatus: string;

      switch (dbState) {
        case 0:
          dbStatus = DB_STATES.DISCONNECTED;
          break;
        case 1:
          dbStatus = DB_STATES.CONNECTED;
          break;
        case 2:
          dbStatus = DB_STATES.CONNECTING;
          break;
        case 3:
          dbStatus = DB_STATES.DISCONNECTING;
          break;
        default:
          dbStatus = DB_STATES.UNKNOWN;
      }

      res.status(STATUS_CODES.OK).json({
        status: MESSAGES.STATUS_OK,
        uptime: process.uptime(),
        dbStatus,
        timestamp: new Date().toISOString(),
      });
    } catch (error) {
      const err = error as APIError;
      logger.error(
        `${err}, route: ${API_ENDPOINTS.HEALTH_CHECK}, Health check failed`
      );
      res.status(STATUS_CODES.INTERNAL_SERVER_ERROR).json({
        status: MESSAGES.INTERNAL_SERVER_ERROR,
        error: err.message,
      });
    }
  });
}

/**
 * Initializes and starts the app
 */
export async function bootstrap(app: Application): Promise<void> {
  initMiddlewares(app);
  initRoutes(app);
  initHealthCheck(app);
  app.use(errorHandler);

  try {
    await connect();
    logger.info("MongoDB connected");
    app.listen(config.port, () =>
      logger.info(`Server running on port ${config.port}`)
    );
  } catch (err) {
    logger.error(`${err}, MongoDB connection error`);
    process.exit(1);
  }
}

// external dependencies
import winston from 'winston';

// Create a Winston logger instance with JSON formatting and timestamps
const logger = winston.createLogger({
  level: process.env.LOG_LEVEL || 'info',
  format: winston.format.combine(
    winston.format.timestamp(),
    winston.format.json()
  ),
  transports: [
    // Log to console
    new winston.transports.Console({
      stderrLevels: ['error'],
      consoleWarnLevels: ['warn'],
    }),
  ],
});

export default logger;

// internal dependencies
import { connectMongo } from "./config";

/**
 * Method to establish mongodb connection
 */
export async function connect() {
    if(!process.env.NODE_ENV) {
        await connectMongo();
    } else {
        // await connectDocumentDB();
    }
}

// external dependencies
import Joi from "joi";
import mongoose from "mongoose";

// internal dependencies
import logger from "./logger";

// schema for expected environment variables
const envSchema = Joi.object({
  MONGO_URI: Joi.string().uri().required().messages({
    "any.required": "MONGO_URI is required",
    "string.uri": "MONGO_URI must be a valid URI",
  }),

  ENCRYPTION_KEY: Joi.string().hex().length(64).required().messages({
    "any.required": "ENCRYPTION_KEY is required",
    "string.length": "ENCRYPTION_KEY must be a 64-character hex string",
  }),

  PORT: Joi.number().port().default(3000),

  LOG_LEVEL: Joi.string()
    .valid("error", "warn", "info", "debug", "trace")
    .default("info"),

  GITHUB_BASE_URL: Joi.string().uri().default("https://api.github.com"),

  CORS_ORIGIN: Joi.string()
    .default("*")
    .messages({ "string.base": "CORS_ORIGIN must be a string" }),
}).unknown(true); // allow other env vars

// Validate process.env against schema
const { value: envVars, error } = envSchema.validate(process.env, {
  abortEarly: false, // report all missing/invalid fields
});

if (error) {
  throw new Error(
    `Environment validation error: ${error.details
      .map((d) => d.message)
      .join(", ")}`
  );
}

// Export a structured config object
export const config = {
  mongoUri: envVars.MONGO_URI as string,
  encryptionKey: envVars.ENCRYPTION_KEY as string,
  port: envVars.PORT as number,
  logLevel: envVars.LOG_LEVEL as string,
  githubBaseUrl: envVars.GITHUB_BASE_URL as string,
  corsOrigin: envVars.CORS_ORIGIN as string,
};

let _isConnected = false;

/**
 * Method to connect mongodb, it checks for MONGO_URL env var
 *
 * @returns the connection status
 */
export async function connectMongo() {
  logger.info("Connecting mongodb");
  if (_isConnected) {
    logger.info("Already connected!");
    return true;
  }
  try {
    const url = config.mongoUri as string;
    await mongoose.connect(url, {
      useNewUrlParser: true,
      useUnifiedTopology: true,
    } as mongoose.ConnectOptions);
    _isConnected = true;
    logger.info("connection successful");
    return true;
  } catch (err) {
    logger.error("Something failed", err);
    return false;
  }
}

export default mongoose;

// external dependencies
import express, { Router } from "express";

// internal dependencies
import { API_ENDPOINTS } from "../common/constants";
import { checkUserExists } from "../middlewares/validateUser";
import { authenticateWithPAT } from "../middlewares/authMiddleware";
import {
  getOpenPRsController,
  getPRTimingMetricsController,
} from "../controllers/prController";
import { GetPrsDto, GetMetricsDto } from "../common/dtos/pr.dto";
import { validateRequest } from "../middlewares/validateRequest";

const router: Router = express.Router();

// GET /prs/:developer/open - Get open PRs for a developer
// this route provides open pull requests for a developer, optionally filtered by repository
router.get(
  API_ENDPOINTS.PRS.OPEN,
  authenticateWithPAT,
  validateRequest(GetPrsDto),
  checkUserExists,
  getOpenPRsController
);

// GET /prs/metrics/:developer - Get PR timing metrics for a developer
// this route provides PR timing metrics for a developer
router.get(
  API_ENDPOINTS.PRS.METRICS,
  authenticateWithPAT,
  validateRequest(GetMetricsDto),
  checkUserExists,
  getPRTimingMetricsController
);

export default router;

// default pagination settings for API endpoints
export const DEFAULT_PAGINATION = Object.freeze({
  LIMIT: 10,
  PAGE: 1,
});

// common error messages used
export const MESSAGES = Object.freeze({
  STATUS_OK: 'OK',
  INTERNAL_SERVER_ERROR: 'Internal Server Error',
  MISSING_TOKEN: 'GitHub Personal Access Token (PAT) is required.',
  INVALID_TOKEN: 'Invalid or expired GitHub token.',
  AUTH_SUCCESS: 'Authenticated successfully',
  USER_EXISTS: 'User already exists',
  USER_CREATED: 'User created successfully',
  USER_DELETED: 'User deleted successfully',
  USER_NOT_FOUND: 'User not found',
  MISSING_DEVELOPER: 'Developer username is required',
  UNAUTHORIZED_USER: 'Unauthorized GitHub username',
});

// HTTP status codes used
export const STATUS_CODES: Record<string, number> = Object.freeze({
  OK: 200,
  CREATED: 201,
  DELETED: 204,
  NOT_FOUND: 404,
  UNAUTHORIZED: 401,
  FORBIDDEN: 403,
  INTERNAL_SERVER_ERROR: 500,
  BAD_REQUEST: 400,
  CONFLICT: 409,
  UNPROCESSABLE_ENTITY: 422,
  TOO_MANY_REQUESTS: 429,
  SERVICE_UNAVAILABLE: 503,
  GATEWAY_TIMEOUT: 504,
});

// gitHub API base URL
export const GITHUB_API_BASE_URL = "https://api.github.com";

// default headers for GitHub API requests
export const GITHUB_API_HEADERS = Object.freeze({
  Accept: "application/vnd.github.v3+json",
  "Content-Type": "application/json",
});

// gitHub API rate limit settings
export const GITHUB_API_RATE_LIMIT = Object.freeze({
  MAX_REQUESTS: 5000, // per hour for authenticated requests
  RESET_TIME: 3600,   // seconds (1 hour)
});

// default port for the application server
export const DEFAULT_PORT = 3000;

// API endpoint paths used in the application
export const API_ENDPOINTS = Object.freeze({
  HEALTH_CHECK: "/health",
  API: "/api",
  AUTH: "/auth",
  PRS: Object.freeze({
    OPEN: "/prs/:developer/open",
    METRICS: "/prs/metrics/:developer",
    ANALYTICS: "/prs/analytics"
  }),
  USER: "/users",
  SEARCH: Object.freeze({
    ISSUES: "/search/issues"
  })
});

export enum DB_STATES {
  DISCONNECTED = "disconnected",
  CONNECTED = "connected",
  CONNECTING = "connecting",
  DISCONNECTING = "disconnecting",
  UNKNOWN = "unknown"
}

// possible states for GitHub pull requests
export const GITHUB_STATES = Object.freeze({
  OPEN: "open",
  CLOSED: "closed",
  MERGED: "merged"
});

// external dependencies
import { Request, Response, NextFunction } from "express";

// internal dependencies
import User from "../models/user";
import { APIError } from "../common/types";
import { MESSAGES, STATUS_CODES } from "../common/constants";

/**
 * Middleware to check if a developer (user) exists in the database
 *
 * @param req - express request object
 * @param res - express response object
 * @param next - next middleware function
 * @returns promise that resolves to void
 */
export const checkUserExists = async (
  req: Request,
  res: Response,
  next: NextFunction
): Promise<void> => {
  try {
    const userName = req.params.developer || req.query.developer;
    if (!userName) {
      res.status(STATUS_CODES.BAD_REQUEST).json({ error: MESSAGES.MISSING_DEVELOPER });
      return;
    }

    const user = await User.findOne({ userName });
    if (!user) {
      res.status(STATUS_CODES.NOT_FOUND).json({ error: MESSAGES.USER_NOT_FOUND });
      return;
    }

    // user exists, proceed to the next middleware/controller
    next();
  } catch (error) {
    const err = error as APIError;
    res.status(STATUS_CODES.INTERNAL_SERVER_ERROR).json({ error: err.message || MESSAGES.INTERNAL_SERVER_ERROR });
  }
};

// external dependencies
import mongoose, { Schema, Document, Model } from "mongoose";

// mongoose document interface for a User
export interface IUser extends Document {
  userName: string;
  email?: string;
  encryptedPat?: {
    iv: string;
    content: string;
    tag: string;
  };
}

// user schema
const userSchema: Schema<IUser> = new Schema<IUser>(
  {
  userName: {
    type: String,
    required: true,
    unique: true,
    trim: true
  },
    email: {
      type: String,
      required: false,
      trim: true,
      lowercase: true,
    },
  encryptedPat: {
    iv:    { type: String },
    content: { type: String },
    tag:   { type: String },
  }
},
  { timestamps: true }
);

const User: Model<IUser> = mongoose.model<IUser>("User", userSchema);
export default User;

import { Response, NextFunction } from "express";

// internal dependencies
import { STATUS_CODES, MESSAGES } from "../common/constants";
import { APIError, AuthenticatedRequest } from "../common/types";
import logger from "../utils/logger";

/**
 * Middleware to authenticate requests using GitHub Personal Access Token (PAT)
 */
export const authenticateWithPAT = async (
  req: AuthenticatedRequest,
  res: Response,
  next: NextFunction
) => {
  const authHeader = req.headers.authorization;

  if (!authHeader?.startsWith("Bearer ")) {
    res.status(STATUS_CODES.UNAUTHORIZED).json({ error: MESSAGES.MISSING_TOKEN });
    return;
  }

  const pat = authHeader.split("Bearer ")[1]?.trim();
  if (!pat) {
    res.status(STATUS_CODES.UNAUTHORIZED).json({ error: MESSAGES.INVALID_TOKEN });
    return;
  }

  try {
    // 💡 Dynamically import Octokit to support ESM in CommonJS
    const { Octokit } = await import("@octokit/rest");
    const octokit = new Octokit({ auth: pat });

    const { data } = await octokit.users.getAuthenticated();

    req.user = {
      username: data.login,
      token: pat,
    };

    next();
  } catch (error) {
    const err = error as APIError;
    logger.error("GitHub PAT authentication failed:", err.message);
    res.status(STATUS_CODES.UNAUTHORIZED).json({ error: MESSAGES.INVALID_TOKEN });
  }
};

// external dependencies
import type { Response } from "express";
import { cache, generateCacheKey } from "../utils/cache";

// internal dependencies
import {
  fetchOpenPullRequests,
  fetchOpenPullRequestsForAllRepos,
  fetchAllPullRequestsForUser,
} from "../services/githubService";
import { APIError, AuthenticatedRequest } from "../common/types";
import {
  STATUS_CODES,
  MESSAGES,
  GITHUB_STATES,
  DEFAULT_PAGINATION,
} from "../common/constants";
import logger from "../utils/logger";

/**
 * GET /prs/:developer/open - Get open PRs for a developer
 * Controller to get open pull requests for a developer
 *
 * @param req - AuthenticatedRequest request object
 * @param res - express response object
 * @returns promise that resolves to void
 */
export const getOpenPRsController = async (
  req: AuthenticatedRequest,
  res: Response
): Promise<void> => {
  const { developer } = req.params;
  const {
    repo,
    page = DEFAULT_PAGINATION.PAGE,
    limit = DEFAULT_PAGINATION.LIMIT,
  } = req.query;
  const user = req.user;

  if (!user?.token) {
    res
      .status(STATUS_CODES.UNAUTHORIZED)
      .json({ error: MESSAGES.UNAUTHORIZED_USER });
    return;
  }

  const pageNum = Math.max(
    1,
    parseInt(page as string) || DEFAULT_PAGINATION.PAGE
  );
  const limitNum = Math.max(
    1,
    parseInt(limit as string) || DEFAULT_PAGINATION.LIMIT
  );

  try {
    // Generate cache key
    const cacheKey = generateCacheKey("openPRs", {
      developer,
      repo: repo as string,
      page: pageNum,
      limit: limitNum,
    });
    const cached = cache.get(cacheKey);
    if (cached) {
      res
        .status(STATUS_CODES.OK)
        .json({ prs: cached, total: (cached as unknown[]).length });
        logger.info("cached result");
        return;
    }

    const allPRs = repo
      ? await fetchOpenPullRequests(developer, repo as string, user.token)
      : await fetchOpenPullRequestsForAllRepos(developer, user.token);

    const paginated = allPRs.slice(
      (pageNum - 1) * limitNum,
      pageNum * limitNum
    );

    // Cache the paginated result
    cache.set(cacheKey, paginated, 60);

    res.status(STATUS_CODES.OK).json({ prs: paginated, total: allPRs.length });
  } catch (error) {
    const err = error as APIError;
    if (err.status === 403 && err.headers) {
      const remaining = err.headers["x-ratelimit-remaining"];
      const reset = err.headers["x-ratelimit-reset"];
      if (remaining === "0") {
        const resetDate = new Date(Number(reset) * 1000);
        const msg = `GitHub rate limit exceeded. Try again at ${resetDate.toISOString()}`;
        logger.warn(`developer: ${developer}, ${msg}`);
        res.status(STATUS_CODES.TOO_MANY_REQUESTS).json({ error: msg });
        return;
      }
    }
    logger.error("Controller error:", err.message);
    res
      .status(STATUS_CODES.INTERNAL_SERVER_ERROR)
      .json({ error: err.message || MESSAGES.INTERNAL_SERVER_ERROR });
      return;
  }
};

/**
 * GET /prs/metrics/:developer - Get PR timing metrics for a developer
 * Controller to get PR timing metrics for a developer
 *
 * @param req - AuthenticatedRequest request object
 * @param res - express response object
 * @returns promise that resolves to void
 */
export const getPRTimingMetricsController = async (
  req: AuthenticatedRequest,
  res: Response
): Promise<void> => {
  const { developer } = req.params;
  const user = req.user;

  if (!user || !user.token) {
    res
      .status(STATUS_CODES.UNAUTHORIZED)
      .json({ error: MESSAGES.UNAUTHORIZED_USER });
    return;
  }

  const token = user.token;

  try {
    // Generate cache key
    const cacheKey = generateCacheKey("prMetrics", { developer });
    const cached = cache.get(cacheKey);
    if (cached) {
      res.status(STATUS_CODES.OK).json(cached);
      logger.info("cached result");
      return;
    }

    const allPRs = await fetchAllPullRequestsForUser(developer, token);
    const now = new Date().getTime();
    const closedDurations: number[] = [];

    const longestRunningOpenPRs = allPRs
      .filter((pr) => pr.state === GITHUB_STATES.OPEN)
      .map((pr) => {
        const openDuration = now - new Date(pr.createdAt).getTime();
        return {
          title: pr.title,
          author: pr.author,
          createdAt: pr.createdAt,
          status: pr.state,
          repo: pr.pr.split("/pull")[0],
          pr: pr.pr,
          openSince: formatExtendedDuration(openDuration),
        };
      })
      .sort(
        (a, b) =>
          new Date(a.createdAt).getTime() - new Date(b.createdAt).getTime()
      )
      .slice(0, 5);

    for (const pr of allPRs) {
      const created = new Date(pr.createdAt).getTime();
      if (pr.state !== GITHUB_STATES.OPEN && pr.closedAt) {
        const closed = new Date(pr.closedAt).getTime();
        closedDurations.push(closed - created);
      }
    }

    const avgCloseOrMergeTime = closedDurations.length
      ? formatExtendedDuration(
          closedDurations.reduce((a, b) => a + b, 0) / closedDurations.length
        )
      : null;

    const result = {
      developer,
      avgCloseOrMergeTime: avgCloseOrMergeTime,
      longestRunningOpenPRs: longestRunningOpenPRs,
    };

    // Cache metrics
    cache.set(cacheKey, result, 60);

    res.status(STATUS_CODES.OK).json(result);
  } catch (error) {
    const err = error as APIError;
    if (err.status === 403 && err.headers) {
      const remaining = err.headers["x-ratelimit-remaining"];
      const reset = err.headers["x-ratelimit-reset"];
      if (remaining === "0") {
        const resetDate = new Date(Number(reset) * 1000);
        const msg = `GitHub rate limit exceeded. Try again at ${resetDate.toISOString()}`;
        logger.warn(`developer: ${developer}, ${msg}`);
        res.status(STATUS_CODES.TOO_MANY_REQUESTS).json({ error: msg });
        return;
      }
    }
    logger.error(`err: ${err}, Failed to fetch PR metrics`);
    return;
  }
};

/**
 * Helper Function to format a duration of milliseconds into a human-readable string
 *
 * @param ms - duration in milliseconds
 * @returns human-readable string representation of the duration
 */
const formatExtendedDuration = (ms: number): string => {
  const totalSeconds = Math.floor(ms / 1000);
  if (totalSeconds < 60) return `${totalSeconds} secs`;

  const years = Math.floor(totalSeconds / (60 * 60 * 24 * 365));
  const months = Math.floor(
    (totalSeconds % (60 * 60 * 24 * 365)) / (60 * 60 * 24 * 30)
  );
  const weeks = Math.floor(
    (totalSeconds % (60 * 60 * 24 * 30)) / (60 * 60 * 24 * 7)
  );
  const days = Math.floor((totalSeconds % (60 * 60 * 24 * 7)) / (60 * 60 * 24));
  const hours = Math.floor((totalSeconds % (60 * 60 * 24)) / (60 * 60));
  const minutes = Math.floor((totalSeconds % (60 * 60)) / 60);
  const seconds = totalSeconds % 60;

  const parts = [];
  if (years) parts.push(`${years} years`);
  if (months) parts.push(`${months} months`);
  if (weeks) parts.push(`${weeks} weeks`);
  if (days) parts.push(`${days} days`);
  if (hours) parts.push(`${hours} hrs`);
  if (minutes) parts.push(`${minutes} mins`);
  if (seconds) parts.push(`${seconds} secs`);

  return parts.join(", ");
};

// external dependencies
import NodeCache from 'node-cache';

// In-memory cache with a default TTL of 60 seconds
export const cache = new NodeCache({ stdTTL: 60, checkperiod: 120 });

/**
 *
 * Method to create a cache key string by combining a prefix with sorted parameters
 * Ensures that identical parameter objects always produce the same cache key
 *
 * @param prefix - namespace prefix for the key (e.g., 'openPRs')
 * @param params - object containing parameters to include in the key
 * @returns deterministic string key for cache storage and retrieval
 */
export function generateCacheKey(
  prefix: string,
  params: Record<string, string | number | boolean | string[]>
): string {
  // Sort keys to ensure consistent ordering
  const sortedParams: Record<string, string | number | boolean> = {};
  Object.keys(params)
    .sort()
    .forEach((key) => {
      const value = params[key];
      sortedParams[key] = Array.isArray(value) ? value.join(',') : value;
    });

  // Serialize sorted params to JSON
  const serialized = JSON.stringify(sortedParams);

  // Combine prefix and serialized params
  return `${prefix}:${serialized}`;
}

// internal dependencies
import logger from "../utils/logger";
import { GITHUB_STATES } from "../common/constants";
import { githubClient } from "../utils/githubClient";
import { APIError, GitHubPR, GitHubRepo, MappedPR } from "../common/types";

/**
 * Helper function to map a GitHub PR object to a simplified format
 * 
 * @param pr - pull request object from the GitHub API
 * @param repoName - name of the repository the pull request belongs to
 * @returns mapped pull request object
 */
function mapPR(pr: GitHubPR, repoName: string): MappedPR {
  return {
    title: pr.title,
    author: pr.user?.login,
    createdAt: pr.created_at,
    closedAt: pr.closed_at,
    status: pr.state,
    state: pr.state,
    repo: repoName,
    pr: pr.html_url,
  };
}

/**
 * Fetches open pull requests for a specific repository from the GitHub API
 * 
 * @param owner - owner of the repository
 * @param repo - name of the repository
 * @param token - gitHub Personal Access Token (PAT) for authentication
 * @returns list of open pull requests for the specified repository
 */
export const fetchOpenPullRequests = async (
  owner: string,
  repo: string,
  token: string
): Promise<MappedPR[]> => {
  try {
    const client = githubClient(token);
    const { data } = await client.get<GitHubPR[]>(`/repos/${owner}/${repo}/pulls`, {
      params: {
        state: GITHUB_STATES.OPEN,
      },
    });

    return data.map((pr) => mapPR(pr, repo));
  } catch (error) {
    const err = error as APIError;
    logger.error(`GitHub API error (repo-specific):`, err);
    throw new Error(err?.message || "GitHub API call failed");
  }
};

/**
 * Fetches open pull requests for all repositories owned by a specific user from the GitHub API
 *
 * @param owner - owner of the repositories
 * @param token - gitHub Personal Access Token (PAT) for authentication
 * @returns list of open pull requests for all repositories owned by the user
 */
export const fetchOpenPullRequestsForAllRepos = async (
  owner: string,
  token: string
): Promise<MappedPR[]> => {
  try {
    const client = githubClient(token);
    const { data: repos } = await client.get<GitHubRepo[]>(`/users/${owner}/repos`);

    const allPRsResults = await Promise.allSettled(
      repos.map(async (repo) => {
        const { data: prs } = await client.get<GitHubPR[]>(
          `/repos/${owner}/${repo.name}/pulls`,
          {
            params: { state: GITHUB_STATES.OPEN },
          }
        );
        return prs.map((pr) => mapPR(pr, repo.name));
      })
    );

    // flatten and filter out failed results
    return allPRsResults
      .filter((result): result is PromiseFulfilledResult<MappedPR[]> => result.status === "fulfilled")
      .flatMap((result) => result.value);
  } catch (error) {
    const err = error as APIError;
    logger.error(`GitHub API error (owner-level):`, err.message);
    throw new Error(err.message || "Failed to fetch repos or PRs");
  }
};

/**
 * Fetches all pull requests for a specific user from the GitHub API
 *
 * @param userName - gitHub username of the user
 * @param token - gitHub Personal Access Token (PAT) for authentication
 * @returns list of all pull requests for the specified user
 */
export const fetchAllPullRequestsForUser = async (
  userName: string,
  token: string
): Promise<MappedPR[]> => {
  const client = githubClient(token);
  const { data: repos } = await client.get<GitHubRepo[]>(`/users/${userName}/repos`);

  const allPRsResults = await Promise.allSettled(
    repos.map(async (repo) => {
      const { data: prs } = await client.get<GitHubPR[]>(
        `/repos/${repo.owner.login}/${repo.name}/pulls`,
        {
          params: { state: "all" },
        }
      );
      return prs.map((pr) => mapPR(pr, repo.name));
    })
  );

  return allPRsResults
    .filter((result): result is PromiseFulfilledResult<MappedPR[]> => result.status === "fulfilled")
    .flatMap((result) => result.value);
};

